mapper.select:
select * from recipe_info


		no=#{no}


		right=1


		and cate=#{cate}


		or (right=1 and title like #{title})


order by no desc

select * from recipe_info join 
(select recipe_info.no,avg(recipe_reply.rate) as avg from recipe_info join recipe_reply on recipe_info.no = recipe_reply.ino 


		right=1


		and title=#{title}


		and cate=#{cate}


group by recipe_info.no) tmp on recipe_info.no = tmp.no order by avg desc
